import React from 'react';
import {
  VStack,
  HStack,
  Text,
  Spacer,
  Input,
  Button,
  IconButton,
  Badge,
  Box,
} from '@chakra-ui/react';
import { FaTrash, FaEdit } from 'react-icons/fa';

const TodoComponents = ({ todos, deleteTodo, editTodo }) => {
  const [searchTerm, setSearchTerm] = React.useState('');

  const formattedSearchTerm = searchTerm.toLowerCase().replace(/\s+/g, ''); // Remove spaces from search term

  const filteredTodos = todos.filter((todo) => {
    const formattedTodoBody = todo.body.toLowerCase().replace(/\s+/g, ''); // Remove spaces from todo body
    return formattedTodoBody.includes(formattedSearchTerm);
  });

  // Handle delete
  const handleDelete = (todo) => {
    const shouldDelete = window.confirm(`Are you sure you want to delete "${todo.body}"?`);

    if (shouldDelete) {
      deleteTodo(todo.id);
    }
  };

  if (!todos.length) {
    return (
      <Badge colorScheme='cyan' p='4' borderRadius='lg'>
        Enter New Task ðŸ‘‡
      </Badge>
    );
  }

  return (
    <VStack
      width="100%"
      maxW="600px"
      mx="auto"
      mb="4"
      overflowY="auto" // Enable vertical scrollbar
      maxHeight="400px" // Set a fixed height to enable the scrollbar
    >
      <HStack justifyContent="space-between">
        <Input
          flex="1"
          type="text"
          placeholder="Search todos"
          margin-to
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />

        {/* Add clear button */}
        <Button
          onClick={() => setSearchTerm('')}
          colorScheme="green"
          size="sm"
          flexShrink="0"
        >
          Clear
        </Button>
      </HStack>

      {filteredTodos.length > 0 ? (
        filteredTodos.map((todo, index) => (
          <Box
            key={todo.id}
            width="100%"
            bg={index % 2 === 0 ? 'white' : 'gray.100'} 
            p="4" 
            borderRadius="md" 
            boxShadow="md" 
            size="md"
            mb="2" 
          >
            <HStack
              spacing="4"
              width="100%"
              borderWidth="1px"
              borderRadius="md"
              boxShadow="md"
              p="2"
            >
              <Text flex="1">{todo.body}</Text>
              <Spacer />
              <IconButton
                icon={<FaEdit />}
                onClick={() => editTodo(todo.id)}
                aria-label={`Edit ${todo.body}`}
                size={'sm'}
              />
              <Button
                onClick={() => handleDelete(todo)}
                leftIcon={<FaTrash />}
                colorScheme="red"
                size={'sm'}
              >
                Delete
              </Button>
            </HStack>
          </Box>
        ))
      ) : (
        <Text style={{ textAlign: 'center' }}>No results. Create a new one instead.</Text>
      )}
    </VStack>
  );
};

export default TodoComponents;
